import React from "react";
export default function Book(props){
 const [booksAdded,setBooksAdded]=React.useState(0);
 const changeShelf=(e,id)=>{
    
  props.setBooks(props.allBooks.map(x=> x.id==id?{...x,shelf:e.target.value}:x));
 }
 const AddBooK=(e,id)=>{
  setBooksAdded(prev=>prev+1);
  
 }
  const elements=props.Books.map(x=>(
 <li key={x.id}>
  <div className="book">
 <div className="book-top"
 style={{
    width: 128,
    height: 193,
    backgroundImage:
      `url(${x.imageLinks.thumbnail||''})`
  }}>
    <div className="book-shelf-changer">
    <select  defaultValue={x.shelf||'none'} onChange={x.shelf?(e)=>AddBook(e,x.id):(e)=>changeShelf(e,x.id)}>
     <option value="none" disabled>
     Move to...
     </option>
     <option value="currentlyReading">
     Currently Reading
     </option>
     <option value="wantToRead">Want to Read</option>
     <option value="read">Read</option>
     <option value="none">None</option>
     </select>
    </div>
 </div>
<div className="book-title">{x.title}</div>
<div className="book-authors">{x.autors}</div>
 </div>  
 </li>))
 return (
    <ol className="books-grid">
        {elements}
    </ol>
 ) 


}
