import React from "react";

import Book from "./Book";
 
let temp='';
export default function Search(props){
    const [txt,setTxt]=React.useState('');
    const [result,setResult]=React.useState([]);
   console.log(result.map(x=>props.Books.map(y=>{return y.id.includes(x.id)?{...x,shelf:y.shelf}:x})));
    React.useEffect(()=>{
        const searchBook=async ()=>{
    
       try{ const res =await props.search(temp);
        
        if(Array.isArray(res)){
        
        setResult(res);}else setResult([])
        }catch(e){
            setResult([]);
        }
        
    
        
         }
         searchBook()}
    ,[temp] );
    let newarr=[];
    for(let i=0;i<result.length;i++){
        if(props.Book.includes(result[i])){
        newarr.push({...props.Book[i]});
        }
        else{
            newarr.push(result [i])
        }
    }
    return(
        <div className="search-books">
        <div className="search-books-bar">
          <a
            className="close-search"
            
          >
            Close
          </a>
          <div className="search-books-input-wrapper">
            <input
              type="text"
              placeholder="Search by title, author, or ISBN"
              onChange={(e)=>{setTxt(e.target.value);temp=e.target.value;}}
            />
          </div>
        </div>
        <div className="search-books-results">
          <ol className="books-grid">
            <Book allBooks={props.Books} Books={result} setBooks={props.setBooks}/>
          </ol>
        </div>
      </div>
    )
}
